import styled from "styled-components";

export const HomeWrapper = styled.div`
    // overflow: hidden;
    width: 960px;
    margin: 30px auto 0 auto;
`;

export const HomeLeft= styled.div`

    
    width: 625px;
    float: left;
    // background: blue;
    .banner-img{
        width: 625px;
		height: 270px;
    }
`;

export const HomeRight = styled.div`
    width: 240px;
    float: right;
    // background: green;
`;

export const ListItem = styled.div`
    
    overflow: hidden;
    padding: 20px 0;
    border-bottom: 1px solid #f0f0f0;
    .list-pic {
        width: 120px;
        height: 100px;
        float: right;
        border-radius: 10px;
    }
`;

export const ItemInfo = styled.div`
    width: 500px;
    float: left;
    .title{
        line-height: 27px;
		font-size: 18px;
		font-weight: bold;
		color: #333;
    }
    .description{
        font-size:13px;
        line-height: 24px;
        color: #999;
    }
`;

// Recommend component
export const RecommendWrapper = styled.div`
    width: 240px;
    margin-bottom: 20px;
    background: red;
`;

const RecommendItem = styled.div`

`;
