import React from "react";
import {connect} from "react-redux";
import {
    ListItem,
    ItemInfo
} from "../home_style"
class List extends React.Component{
    render(){
        return (
            <div>
                {
                    this.props.articleList.map((item)=>{
                        return(
                            <ListItem key={item.id}>
                                <img className="list-pic" alt="list-pic" src={item.imgUrl}></img>
                                <ItemInfo>
                                    <h2 className="title">{item.articleTitle}</h2>
                                    <p className="description">{item.articleDescription}</p>
                                </ItemInfo>
                            </ListItem>
                        )
                    })
                }
                
                {/* <ListItem>
                    <img className="list-pic" alt="list-pic" src="https://upload-images.jianshu.io/upload_images/11013747-30ef93d2a1f7f4cc.jpg?imageMogr2/auto-orient/strip|imageView2/1/w/360/h/240"></img>
                    <ItemInfo>
                        <h2 className="title">那些扎心的网易云热评（句句扎心）</h2>
                        <p className="description">后来我发现， 太浓烈的东西总是不长久 应了那句老话 第一眼就心动的人没法做朋友 ——网易云音乐热评《阁楼》 ​​​ “希望你以后能过得好，别辜...</p>
                    </ItemInfo>
                </ListItem> */}
            </div>
        )
    }
}

const mapStateToProps = (state) =>{
    return {
        articleList: state.home.articleList
    }
}

export default connect(mapStateToProps, null)(List);
