import React from "react";
import {connect} from "react-redux";
import {
    DetailWrapper,
    Title,
    Author,
    AuthorInfo,
    Content
} from "./detail_style";

class Detail extends React.Component{
    render(){
        const {content, title, author} = this.props;
        return (
            <DetailWrapper>
                <Title>{title}</Title>
                <Author>
                    <img alt="author-img" className="author-img" src="https://upload.jianshu.io/users/upload_avatars/11013747/6f18f516-5aa9-4c22-92fc-45ca43335e95.jpg?imageMogr2/auto-orient/strip|imageView2/1/w/96/h/96"></img>
                    <AuthorInfo>{author}</AuthorInfo>
                </Author>
                <Content dangerouslySetInnerHTML={{__html:content}} />
            </DetailWrapper>
        )
    }
}

const mapStateToProps = (state) =>  {
    return {
        title: state.detail.title,
        author: state.detail.author,
        content: state.detail.content
    }
}

export default connect(mapStateToProps, null)(Detail);
