import React from "react";
import List from "./component/list";
import Recommend from "./component/recommend";
import axios from "axios";
import { connnect } from "react-redux"; 
import Writer from "./component/writer";

import {
    HomeWrapper,  
    HomeLeft,
    HomeRight
} from "./home_style";


class Home extends React.Component{

    componentDidMount(){
        axios.get("/api/home.json").then((res) => {
            console.log(res.data.homeData);
            const action = {
                type: "set_articleList",
                articleList: res.data.homeData.articleList
            };
        })
    }
    
    render(){
        return (
            <HomeWrapper>
                <HomeLeft>
                    <img className="banner-img" alt="banner-img" src="https://upload.jianshu.io/admin_banners/web_images/4660/224da83c76e01d5deff07e163615921233af5c82.jpg?imageMogr2/auto-orient/strip|imageView2/1/w/1250/h/540"></img>
                    <List></List>
                </HomeLeft>
                <HomeRight>
                    <Recommend></Recommend>
                    <Writer></Writer>
                </HomeRight>
            </HomeWrapper>
        )
    }
}
export default Home;
