import styled from "styled-components";

export const LoginWrapper = styled.div`
    // height: 100%;
    // // min-height: 750px;
    position: absolute;
    left: 0;
    right: 0;
    top: 56px;
    bottom: 0;
    text-align: center;
    font-size: 14px;
    background-color: #f1f1f1;
`;

export const LoginBox = styled.div`
    width: 400px;
    margin: 40px auto;
    padding: 50px 50px;
    border-radius: 4px;
    box-shadow: 0 0 8px rgba(0,0,0,.1);
    background: #fff;
    box-sizing: border-box;
    .login-title{
        display: block;
        margin: 0 auto 50px;
        padding: 10px;
        font-weight: 400;
        color: #969696;
    }
`;

export const Input = styled.input`
    width: 100%;
    height: 50px;
`;
