import React from "react";
import { connect } from "react-redux";
import {
    HeaderWrapper, 
    Logo,
    Nav,
    NavItem,
    SearchWrapper,
    NavSearch,
    SearchInfo,
    SearchInfoTitle,
    SearchInfoChange,
    SearchInfoItem,
    Addition,
    Button
} from "./header_style";


const getListArea = (show) => {
    if(show) {
        return (
            <SearchInfo>
                    <SearchInfoTitle>
                        Popular
                        <SearchInfoChange>Change</SearchInfoChange>
                    </SearchInfoTitle>
                    <div>
                        <SearchInfoItem>教育</SearchInfoItem>
                        <SearchInfoItem>教育</SearchInfoItem>
                        <SearchInfoItem>教育</SearchInfoItem>
                        <SearchInfoItem>教育</SearchInfoItem>
                        <SearchInfoItem>nimemsldfklsdf</SearchInfoItem>
                        <SearchInfoItem>s</SearchInfoItem>
                    </div>
                </SearchInfo>
        )
    }else{
        return null;
    }
}


const Header = (props) => {
    return (
        <HeaderWrapper>
        <Logo></Logo>
        <Nav>
            <NavItem className="left active">首页</NavItem>
            <NavItem className="left">下载App</NavItem>
            <NavItem className="right">登陆</NavItem>
            <NavItem className="right"><span className="iconfont">&#xe636;</span></NavItem>
            <SearchWrapper>
                <NavSearch onFocus={props.handleFocus}></NavSearch>
                <span className="iconfont">&#xe637;</span>
                {getListArea(props.focused)}
            </SearchWrapper>
            
        </Nav>
        <Addition>
            <Button className="writing"><span className="iconfont">&#xe603;</span>写文章</Button>
            <Button>注册</Button>
        </Addition>

    </HeaderWrapper>
    )
}
