import styled from "styled-components";
import logo from "../../static/logo.png";

export const HeaderWrapper = styled.div`
    height: 56px;
    border-bottom: 1px solid #f0f0f0;   
`;

export const Logo = styled.a.attrs({
    href: "/"
})`
    position: absolute;
    left: 0;
    top: 0;
    display: block;
    width: 100px;
    height: 56px;
    background: url(${logo});
    background-size: contain;
`;

export const Nav = styled.div`
    // background-color: green;
    width: 960px;
    height: 100%;
    margin: 0 auto;
`;

export const NavItem = styled.div`
    line-height: 56px;
    font-size: 17px;
    padding: 0 15px;
    background-color: red;
    color: #333333;
    &.left{
        float: left;
    }
    &.right{
        float: right;
    }
    &.active{
        color:#ea6f5a;
    }
`;

export const SearchWrapper = styled.div`
    position: relative;
    float: left;
    .iconfont{
        position: absolute;
        right: 5px;
        bottom: 9px;
    }
`;

export const NavSearch = styled.input.attrs({
    placeholder: 'search'    
})`
    font-size: 15px;
    height: 38px;
    width: 160px;
    padding: 0 20px;
    box-sizing: border-box;
    border: none;
    outline: none;
    border-radius: 19px;
    margin-top: 9px;
    background: #eee;
`;

export const Addition = styled.div`
        position: absolute;
        right: 0;
        top: 0;
        height: 56px;
        // background: green;
`;

export const Button = styled.div`
    float: right;
    line-height: 38px;
    margin-top: 9px;
    border:1px solid rgba(236,97,73,.7);
    border-radius: 19px;
    padding: 0 20px;
    margin-right: 30px;
    font-size: 14px;

    &.writing{
        background-color:#ea6f5a;
        color: white;
    }
`;
