const defaultState = {
    title: "那些扎心的网易云热评（句句扎心）",
    author: "夏有乔木",
    content: '<p>后来我发现,太浓烈的东西总是不长久应了那句老话第一眼就心动的人没法做朋友 </p><p className="songName"> ——《阁楼》 ​​​</p><p>希望你以后能过得好，别辜负我一生不打扰。​​​</p><p className="songName"> —— </p>'
}

export default (state=defaultState, action) => {

    switch(action.type){
        default:
            return state;
    }
}
