import React from "react";
import List from "./component/list";
import Recommend from "./component/recommend";
import axios from "axios";
import { connect } from "react-redux"; 
import Writer from "./component/writer";
import {actionCreators} from "./store/index";


import {
    HomeWrapper,  
    HomeLeft,
    HomeRight
} from "./home_style";


class Home extends React.Component{

    componentDidMount(){
        this.props.getHomePage();
    }
    
    render(){
        return (
            <HomeWrapper>
                <HomeLeft>
                    <img className="banner-img" alt="banner-img" src="https://upload.jianshu.io/admin_banners/web_images/4660/224da83c76e01d5deff07e163615921233af5c82.jpg?imageMogr2/auto-orient/strip|imageView2/1/w/1250/h/540"></img>
                    <List></List>
                </HomeLeft>
                <HomeRight>
                    <Recommend></Recommend>
                    <Writer></Writer>
                </HomeRight>
            </HomeWrapper>
        )
    }
}

const mapDispatchToProps = (dispatch) =>{
    return {
        getHomePage() {
            axios.get("/api/home.json").then((res) => {
                const homeData = res.data.homeData;
                this.props.getHomePage(homeData);
            })
            dispatch(actionCreators.setHomeData(homeData));
        }
    }
};

export default connect(null, mapDispatchToProps)(Home);
