import * as actionType from "./actionType";

export default (state, action) => {
    const newState = JSON.parse(JSON.stringify(state));
    switch(action.type){
        case actionType.SEARCH_FOCUS:
            newState.focused = ! newState.focused;
            return newState;
        default:
            return state;
    }
};
