import styled from "styled-components";

export const DetailWrapper = styled.div`
    width: 620px;
    margin: 0px auto;
    // background: red;  
`;

export const Title = styled.div`
    width: 100%;
    margin: 30px 0px;
    text-align: center;
    font-size: 34px;
    color: #333333;
    font-weight: bold;
`;

export const Content = styled.div`
    margin: 50px 0;
    color:#2f2f2f;
    font-size: 16px;
    line-height: 30px;
`;

export const Author = styled.div`
    width: 100%;
    .author-img{
        width: 48px;
        height: 48px;
        margin-right: 20px;
    }
`;

export const AuthorInfo = styled.div`
    display: inline-block;
    font-size: 16px;
`;
