import React from "react";
import { connect } from "react-redux";
import {actionCreators} from "./headerStore/index";
import {
    HeaderWrapper, 
    Logo,
    Nav,
    NavItem,
    SearchWrapper,
    NavSearch,
    SearchInfo,
    SearchInfoTitle,
    SearchInfoChange,
    SearchInfoItem,
    Addition,
    Button
} from "./header_style";


class Header extends React.Component{
    
    getListArea = () => {
        const {focused, list, handleMouseEnter, mouseIn} = this.props;
        if(focused || mouseIn) {
            return (
                <SearchInfo 
                onMouseEnter={handleMouseEnter}
                onMouseLeave={handleMouseLeave}>
                        <SearchInfoTitle>
                            Popular
                            <SearchInfoChange>Change</SearchInfoChange>
                        </SearchInfoTitle>
                        <div>
                            {
                                list.map((element)=>{
                                    return (
                                        <SearchInfoItem key={element}>{element}</SearchInfoItem>
                                    )        
                                })
                            }
                        </div>
                    </SearchInfo>
            )
        }else{
            return null;
        }
    }

    render(){
        const { handleFocus, handleBlur} = this.props;
        return (
            <HeaderWrapper>
                <Logo></Logo>
                <Nav>
                    <NavItem className="left active">首页</NavItem>
                    <NavItem className="left">下载App</NavItem>
                    <NavItem className="right">登陆</NavItem>
                    <NavItem className="right"><span className="iconfont">&#xe636;</span></NavItem>
                    <SearchWrapper>
                        <NavSearch 
                        onFocus={handleFocus}
                        onBlur ={handleBlur}>
                        </NavSearch>
                        <span className="iconfont">&#xe637;</span>
                        {this.getListArea()}
                    </SearchWrapper>
                    
                </Nav>
                <Addition>
                    <Button className="writing"><span className="iconfont">&#xe603;</span>写文章</Button>
                    <Button>注册</Button>
                </Addition>
            </HeaderWrapper>
        )

    }
}

const mapStateToProps = (state) => {
    return {
        focused: state.get("header").get("focused"),
        list: state.get("header").get("list"),
        page: state.get("header").get("page"),
        mouseIn: state.get("header").get("mouseIn")
    }
}

const mapDispatchToProps = (dispatch) =>{
    return {
        handleFocus(){ 
           
            dispatch(actionCreators.searchPopular());
            dispatch(actionCreators.searchFocus());
        },
        handleBlur(){
            dispatch(actionCreators.searchBlur());
        },

        handleMouseEnter(){
            dispatch(actionCreators.mouseEnter());
        },
        handleMouseLeave(){
            console.log("dfs");
        }

    
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Header);
